
public class Whiledemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//while loop - print 1 to 10
		//use boolean value for while loop
		//do operatiom on that variable
		int i=0;
		while(i<10)
		{
			System.out.println(i);
			i++;
		}
		while(i>0)
		{
			System.out.println(i);
			i--;
		}
	}

}
